import React, {Component} from 'react';
import ItemCard from './components/itemcard';
import ItemSort from './components/sort';
import ItemFilter from './components/filter';
import FilterLayer from './components/filterLayer';
import axios from 'axios';

class Listpage extends Component {
	state = {
		renderList: [true,false,false],
		itemLists : [],
		itemFreeDeliveryLists : [],
		viewTypeNumber : 0,
		filterToggle:true,
		filterActive : false,
		activeList : [],
		Itemcard: [],//아이템카드 리스트
    	ItemcardBasic:[],
	}

	renderItem = (addFavorite) =>{

		const number = this.state.Itemcard.length;
		let items = this.state.Itemcard.slice(0,number).map((Itemcard,index) => 
		<Itemlist ranking={Itemcard.ranking}
			GoodsCode={Itemcard.GoodsCode}
			ImageURL={Itemcard.ImageURL}
			BrandName={Itemcard.BrandName}
			GoodsName={Itemcard.GoodsName}
			SalePrice={Itemcard.SalePrice}
			DeliveryText={Itemcard.Delivery.DeliveryText}
			DeliveryInfo={Itemcard.Delivery.DeliveryInfo}
			BuyCount={Itemcard.BuyCount}
			key={index}
			addFavorite={addFavorite}
			IsFavoriteSeller ={Itemcard.IsFavoriteSeller}
		/>
		);

		return items
	  }

    _callApi = () => {
        return axios('./SearchJson.json')
        .then(({ data })=> {
            this.setState({ 
				itemLists: data.Item
			});
        })
		.catch(err => console.log(err))
	}

	//필터 온오프 클래스 불린값 설정 함수
	filterToggle = () => {
		//const toggle = this.state.filterActive ? 'false' : 'true'
		this.setState({
			filterActive : !this.state.filterActive
		})
	}

	// 필터 레이어 닫기 함수
	layerClose = () => {
		this.setState({
			filterActive : false
		})
	}

	// 리스트뷰 타입 넘버 받아오는 함수
	viewTypeFunc = (typeNumber) => {
		this.setState({
			viewTypeNumber : typeNumber
		})
	}
	//랭킹순
	sortRank = () => {
		const itemListsLength = this.state.itemList.length;
		const sortList = this.state.itemLists.slice(0,itemListsLength).sort(function(a,b){
			return a['ranking'] - b['ranking'];
		});
		this.setState({
			renderList: [true,false,false],
			ItemcardBasic : sortList
		})
	}

	//낮은 가격순
	sortRowPrice = () => {
		const itemListsLength = this.state.itemList.length;
		const sortList = this.state.itemLists.slice(0,itemListsLength).sort(function(a,b){
			return a['SellPrice'] - b['SellPrice'];
		})
		this.setState({
			renderList: [false,false,true],
			ItemcardBasic : sortList
		})
	}

	//구매순
	sortBuyCount = () => {
		const itemListsLength = this.state.itemList.length;
		const sortList = this.state.itemLists.slice(0,itemListsLength).sort(function(a,b){
			return b['BuyCount'] - a['BuyCount'];
		});
		this.setState({
			renderList: [false,true,false],
			ItemcardBasic : sortList
		})
	}
	//무료배송
	freeDeliveryFunc = () => {
		const itemListsLength = this.state.itemList.length;
		const itemFreeDeliveryLists = this.state.itemLists.slice(0,itemListsLength).filter((itemList) => {
			return itemList.Delivery.DeliveryText === "무료배송"
		});
		this.setState({
			renderList: [false,true,false],
			ItemcardBasic : itemFreeDeliveryLists
		})
	}

    componentDidMount(){
		this._callApi();
    }
	render() {
		const {addCart} = this.props
		//const filterActive = this.state;
		return (
			<div id="content" className={this.state.viewTypeNumber ? 'state--content_view_type__list' : 'state--content_view_type__gallery'}>
				<div className="section--content_body_container">
					<div id="region--content_status_information">   
						<div className="section--module_wrap">
							<div className="section--search_relative_information section--content_information_bar_container">
								<div className="text--search_result">
									<span className="text--item_count result--text_color">{this.state.itemLists.length}</span>
									<span className="text--description"><em className="result--text_color">개</em>의 검색 결과</span>
								</div>
								<ItemSort renderList={this.state.renderList} sortRowPrice={this.sortRowPrice} sortBuyCount={this.sortBuyCount} sortRank={this.sortRank} makeItemList={this._makeItemList}/>
								<button type="button" className="link--button_filter link_active" onClick={this.filterToggle}>필터</button>
							</div>
							<ItemFilter/>           
						</div>
					</div>
					<FilterLayer filterActive={this.state.filterActive} layerClose={this.layerClose} viewTypeFunc={this.viewTypeFunc} viewTypeNumber={this.state.viewTypeNumber}/>
					<div id="region--content_body">
						<div id="section--inner_content_body_container">
							<div className="section--module_wrap">
								{this.sortRank(this._makeItemList)}
							</div>
						</div>
					</div>
					<div>

					</div>
				</div>
			</div>
		)
	}
}
export default Listpage;
