import React, {Component} from 'react';
import axios from 'axios';
import _ from 'lodash';

class ItemCard extends Component {
    constructor(props) {
        super(props);

        this.state = {
          array: []
        };
        this.renderItemList = this.renderItemList.bind(this);
      }

      componentDidMount(){
        axios
          .get('./SearchJson.json')
          .then(({ data })=> {
            console.log(data);
            this.setState(
              { array: data.Item }
            );
          })
          .catch((err)=> {})
      }

    render() {
        return (
            <div>
                {this.renderItemList()}
            </div>
        )
    }

    renderItemList() {
      //console.log(this.state.array)
        return _.map(this.state.array, itemdata => {
            return (
                <div className="component component--item_card">
                    <div className="itemcard">            
                        <div className="section--itemcard_box">    
                            <div className="section--itemcard">
                                <div className="itemcard--inner">
                                    <a href={itemdata.ImageURL} className="link--itemcard">
                                        <span className="section--itemcard_img"><img src={itemdata.ImageURL} className="image--itemcard" alt=""></img></span>
                                        <span className="section--itemcard_info">
                                            <span className="area--itemcard_title">
                                                <span className="text--brand">{itemdata.BrandName}</span>
                                                <span className="text--title">{itemdata.GoodsName}</span>
                                            </span>
                                            <span className="area--itemcard_price">
                                                <span className="price_seller">
                                                    <strong className="text--price_seller">{itemdata.SalePrice}</strong><span className="text--unit">원</span>
                                                </span>
                                            </span>
                                            <span className="section--itemcard_info_add">
                                                <span className="text--addinfo">{itemdata.DeliveryText}</span>
                                                <span className="text--addinfo">{itemdata.DeliveryInfo}</span>                                               
                                            </span>
                                            <span className="section--itemcard_info_score">
                                                <span className="score--buycnt">
                                                    <span className="text--buycnt">구매</span><span className="text--buycnt__num">{itemdata.BuyCount}</span><span className="ir">건</span>
                                                </span>
                                                <span className="score--awards"><span className="ir">만족도</span><span className="text--awards">4.8</span><span className="ir">점</span></span>                                                
                                            </span>
                                        </span>                                        
                                    </a>
                                    {this.renderItemCart()}              
                                </div>                                                            
                            </div>
                        </div>
                    </div>
                </div>
            );
        });
    }
    renderItemCart() {
        return (
            <div>
                <button className="button--favorite"><span className="icon--favorite"><span className="ir">관심상품 등록하기</span></span></button>   
                <button className="button--cart element-info-cart"><span className="icon--cart"><span className="ir">장바구니담기</span></span></button>  
            </div>
        );
    }

}
export default ItemCard;
